properties.widgets.DecisionTreeTrainer-sparksink:
{
  "outputs": [],
  "metadata": {"spec-version": "1.0"},
  "configuration-groups": [{
    "label": "Decision Tree Trainer Properties",
    "properties": [
      {
        "widget-type": "textbox",
        "name": "fileSetName",
        "label": "FileSet Name"
      },
      {
        "widget-type": "textbox",
        "name": "path",
        "label": "Path"
      },
      {
        "widget-type": "csv",
        "name": "featureFieldsToInclude",
        "label": "Features to Include for Training",
        "widget-attributes": {
          "value-placeholder": "Field Name",
          "delimiter": ","
        }
      },
      {
        "widget-type": "csv",
        "name": "featureFieldsToExclude",
        "label": "Features to Exclude while Training",
        "widget-attributes": {
          "value-placeholder": "Field Name",
          "delimiter": ","
        }
      },
      {
        "widget-type": "keyvalue",
        "name": "cardinalityMapping",
        "label": "Categorical Features Information",
        "widget-attributes": {"showDelimiter": "false"}
      },
      {
        "widget-type": "textbox",
        "name": "labelField",
        "label": "Label Field"
      },
      {
        "widget-type": "number",
        "name": "maxDepth",
        "label": "Maximum Depth of Tree",
        "widget-attributes": {
          "default": "10",
          "min": 2
        }
      },
      {
        "widget-type": "number",
        "name": "maxBins",
        "label": "Maximum Number of Bins",
        "widget-attributes": {
          "default": "100",
          "min": 2
        }
      }
    ]
  }]
}

