properties.widgets.CobolRecordConverter-transform:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [{
    "label": "COBOL to Avro Record Converter Configuration",
    "properties": [
      {
        "widget-type": "textarea",
        "name": "copybook",
        "label": "Copybook",
        "widget-attributes": {"rows": "4"},
        "plugin-function": {
          "widget": "outputSchema",
          "missing-required-fields-message": "'Copybook' is required to fetch schema.",
          "method": "POST",
          "required-fields": ["copybook"],
          "output-property": "schema",
          "plugin-method": "outputSchema"
        }
      },
      {
        "widget-type": "select",
        "name": "codeFormat",
        "label": "Code Format for Copybook",
        "widget-attributes": {
          "default": "FIXED_FORMAT",
          "values": [
            "FIXED_FORMAT",
            "FREE_FORMAT"
          ]
        }
      },
      {
        "widget-type": "textbox",
        "name": "charset",
        "label": "Charset",
        "widget-attributes": {"default": "IBM01140"}
      },
      {
        "widget-type": "select",
        "name": "rdw",
        "label": "Records start with Record Descriptor Word",
        "widget-attributes": {
          "default": "true",
          "values": [
            "true",
            "false"
          ]
        }
      },
      {
        "widget-type": "textbox",
        "name": "fieldName",
        "label": "Name of the field containing COBOL records"
      }
    ]
  }],
