properties.widgets.DatabaseArgumentSetter-action:
{
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Connection",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "jdbcPluginName",
          "label": "Plugin Name"
        },
        {
          "widget-type": "textbox",
          "name": "jdbcPluginType",
          "label": "Plugin Type",
          "widget-attributes": {"default": "jdbc"}
        },
        {
          "widget-type": "textbox",
          "name": "connectionString",
          "label": "Connection String"
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Configuration",
      "properties": [
        {
          "widget-type": "text",
          "name": "databaseName",
          "label": "Database"
        },
        {
          "widget-type": "text",
          "name": "tableName",
          "label": "Table"
        },
        {
          "widget-type": "keyvalue",
          "name": "argumentSelectionConditions",
          "label": "Argument Selection Conditions",
          "widget-attributes": {
            "kv-delimiter": "=",
            "key-placeholder": "Key",
            "value-placeholder": "Value",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        },
        {
          "widget-type": "text",
          "name": "argumentsColumns",
          "label": "Arguments column"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [{
        "widget-type": "keyvalue",
        "name": "connectionArguments",
        "label": "Connection Arguments",
        "widget-attributes": {
          "kv-delimiter": "=",
          "key-placeholder": "Key",
          "value-placeholder": "Value",
          "delimiter": ";",
          "showDelimiter": "false"
        }
      }]
    }
  ],
  "display-name": "Database Argument Setter"
}
