properties.widgets.DynamoDB-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "default-schema": {
        "name": "etlSchemaBody",
        "type": "record",
        "fields": [{
          "name": "body",
          "type": "string"
        }]
      },
      "widget-attributes": {
        "schema-types": [
          "boolean",
          "int",
          "long",
          "float",
          "double",
          "bytes",
          "string"
        ],
        "schema-default-type": "string"
      }
    }
  }],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [{
    "label": "DynamoDB Configuration",
    "properties": [
      {
        "widget-type": "textbox",
        "name": "referenceName",
        "label": "Reference Name"
      },
      {
        "widget-type": "password",
        "name": "accessKey",
        "label": "Access Key"
      },
      {
        "widget-type": "password",
        "name": "secretAccessKey",
        "label": "Secret Access Key"
      },
      {
        "widget-type": "select",
        "name": "region",
        "label": "Region",
        "widget-attributes": {
          "default": "us-west-2",
          "values": [
            "us-gov-west-1",
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "eu-west-1",
            "eu-west-2",
            "eu-central-1",
            "ap-south-1",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-northeast-1",
            "ap-northeast-2",
            "sa-east-1",
            "cn-north-1",
            "ca-central-1"
          ]
        }
      },
      {
        "widget-type": "textbox",
        "name": "endpointUrl",
        "label": "Endpoint URL"
      },
      {
        "widget-type": "textbox",
        "name": "tableName",
        "label": "Table Name"
      },
      {
        "widget-type": "textbox",
        "name": "query",
        "label": "Query"
      },
      {
        "widget-type": "textbox",
        "name": "filterQuery",
        "label": "Filter Query"
      },
      {
        "widget-type": "keyvalue",
        "name": "nameMappings",
        "label": "Attribute Name Map",
        "widget-attributes": {
          "kv-delimiter": "|",
          "key-placeholder": "Attribute Name Placeholder",
          "value-placeholder": "Placeholder Value",
          "showDelimiter": "false"
        }
      },
      {
        "widget-type": "keyvalue",
        "name": "valueMappings",
        "label": "Attribute Value Map",
        "widget-attributes": {
          "kv-delimiter": "|",
          "key-placeholder": "Attribute Value Placeholder",
          "value-placeholder": "Placeholder Value",
          "showDelimiter": "false"
        }
      },
      {
        "widget-type": "keyvalue-dropdown",
        "name": "placeholderType",
        "label": "Placeholders and Its type",
        "widget-attributes": {
          "kv-delimiter": "|",
          "dropdownOptions": [
            "boolean",
            "int",
            "long",
            "float",
            "double",
            "string"
          ],
          "key-placeholder": "Placeholder Type",
          "showDelimiter": "false"
        }
      },
      {
        "widget-type": "textbox",
        "name": "readThroughput",
        "label": "Read Throughput"
      },
      {
        "widget-type": "textbox",
        "name": "readThroughputPercentage",
        "label": "Read Throughput Percentage"
      }
    ]
  }],
