properties.widgets.SalesforceDataExtension-batchsin:
{
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name"
        },
        {
          "widget-type": "textbox",
          "name": "dataExtension",
          "label": "Data Extension External Key"
        },
        {
          "widget-type": "radio-group",
          "name": "operation",
          "label": "Operation",
          "widget-attributes": {
            "layout": "inline",
            "default": "insert",
            "options": [
              {
                "id": "insert",
                "label": "Insert"
              },
              {
                "id": "update",
                "label": "Update"
              },
              {
                "id": "upsert",
                "label": "Upsert"
              }
            ]
          }
        }
      ]
    },
    {
      "label": "Authentication",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "clientId",
          "label": "Client ID"
        },
        {
          "widget-type": "password",
          "name": "clientSecret",
          "label": "Client Secret"
        },
        {
          "widget-type": "textbox",
          "name": "authEndpoint",
          "label": "Authentication Base URI"
        },
        {
          "widget-type": "textbox",
          "name": "soapEndpoint",
          "label": "SOAP Base URI"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "truncateText",
          "label": "Truncate Text",
          "widget-attributes": {
            "layout": "inline",
            "default": "true",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
          "widget-type": "radio-group",
          "name": "failOnError",
          "label": "Fail On Error",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
          "widget-type": "radio-group",
          "name": "replaceWithSpaces",
          "label": "Replace Underscores With Spaces",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "maxBatchSize",
          "label": "Max Batch Size",
          "widget-attributes": {"default": "500"}
        },
        {
          "widget-type": "keyvalue",
          "name": "columnMapping",
          "label": "Column Mapping",
          "widget-attributes": {
            "kv-delimiter": "=",
            "key-placeholder": "Input Field",
            "value-placeholder": "Data Extension Column",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        }
      ]
    }
  ],
